import React from "react";
import styled from "styled-components";

const Content = styled.div`
    display: flex;
    width: max-content;
    align-items: flex-start;
    justify-content: center;
    gap: 6px;
    padding: 8px 10px;
    background: var(--color--green-surface);
    border: 1px solid var(--color--green-border);
    border-radius: 16px;

    @media (max-width: 768px){
        position: absolute;
        align-items: center;
        top: 20px;
        width: auto;
        right: 20px;
        border-radius: 12px;
        z-index: 2;
        background: var(--color--black-surface);
        backdrop-filter: blur(2px);
        border: none;
    }

    & svg {
        width: max-content;
        height: 34px;
        fill: var(--color--green);

        @media (max-width: 768px){
            fill: var(--color--white);
            height: 30px;
            width: 30px;
        }
    }

    & aside {
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        justify-content: center;
        gap: 2px;
        width: auto;

        & span {
            font-size: 18px;
            font-weight: 600;
            color: var(--color--green);

            @media (max-width: 768px){
                color: var(--color--white);
                font-weight: 500;
                font-size: 16px;
            }
        }

        & p {
            font-size: 14px;
            font-weight: 300;
            color: var(--color--green);

            @media (max-width: 768px){
                color: var(--color--white);
                font-size: 12px;
            }
        }
    }
`

